CREATE TABLE IF NOT EXISTS `PFW_gvisions_news` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `author` int(10) NOT NULL DEFAULT '0',
  `titel` text NOT NULL,
  `text` text NOT NULL,
  `tags` text NOT NULL,
  `date` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Newsmanagement';

CREATE TABLE IF NOT EXISTS `PFW_gvisions_news_comments` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `date` varchar(50) NOT NULL DEFAULT '0',
  `author` varchar(50) NOT NULL DEFAULT '0',
  `text` text NOT NULL,
  `newsid` int(10) unsigned NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

